package server;public class CustomerPK implements java.io.Serializable {		private int customerNumber;		private String firstname;		private String postalCode;		public CustomerPK() {	}		public CustomerPK(int customerNumber, String firstname, String postalCode) {		this.customerNumber = customerNumber;		this.firstname = firstname;		this.postalCode = postalCode;	}		public int getCustomerNumber() {		return customerNumber;	}		public String getFirstname() {		return firstname;	}		public String getPostalCode() {		return postalCode;	}		public void setCustomerNumber(int customerNumber) {		this.customerNumber = customerNumber;	}		public void setFirstname(String firstname) {		this.firstname = firstname;	}		public void setPostalCode(String postalCode) {		this.postalCode = postalCode;	}		public boolean equals(Object obj) {		if (obj == this)			return true;		if (!(obj instanceof CustomerPK))			return false;		CustomerPK pk = (CustomerPK) obj;		return (this.customerNumber == pk.customerNumber && this.firstname.equals(pk.firstname) && this.postalCode.equals(pk.postalCode));	}		public int hashCode() {		return (int) this.customerNumber + this.firstname.hashCode() + this.postalCode.hashCode();	}}